{{groovy}}
lines = 2000
if (request.logfile) {
  logfile = request.logfile;
} else {
  logfile = "/usr/local/tomcat/logs/catalina.out"
}
if(request.showlog == "1") {
  line = "/usr/bin/tail -n " + lines + " " + logfile
  print("{{code language='none'}}")
  print(line.execute().getText()) 
  print("{{/code}}")
}
{{/groovy}}
